sect2.programlisting:
begin
 acs_privilege.create_privilege('read');
 acs_privilege.create_privilege('write');
 acs_privilege.create_privilege('create');
 acs_privilege.create_privilege('delete');
 acs_privilege.create_privilege('admin');

 acs_privilege.add_child('admin', 'read');
 acs_privilege.add_child('admin', 'write');
 acs_privilege.add_child('admin', 'create');
 acs_privilege.add_child('admin', 'delete');

 commit;
end;

acs_permission.grant_permission (
  object_id => some_object_id,
  grantee_id => some_party_id,
  privilege => 'some_privilege_name'
  );

# main index page for notes.
ad_page_contract { @author you @cvs-id $Id$ } -properties { notes:multirow context_bar:onevalue create_p:onevalue }
set package_id [ad_conn package_id] set user_id [ad_conn user_id]
set context_bar [ad_context_bar] set create_p [permission::permission_p \ -object_id $package_id \ -privilege create \ -party_id $user_id]
db_multirow notes notes { select note_id, owner_id, title, body, decode(acs_permission.permission_p(note_id, :user_id, 'write'), 't', 1, 'f', 0) as write_p, decode(acs_permission.permission_p(note_id, :user_id, 'admin'), 't', 1, 'f', 0) as admin_p, decode(acs_permission.permission_p(note_id, :user_id, 'delete'), 't', 1, 'f', 0) as delete_p from notes n, acs_objects o where n.note_id = o.object_id and o.context_id = :package_id and acs_permission.permission_p(note_id, :user_id, 'read') = 't' order by creation_date }
